mapper.select:
/* 사용자 아이디 / 비번 체크 */ SELECT U.USER_ID AS USER_ID , U.PASSWORD AS PASSWORD , U.EMAIL AS EMAIL , U.NICKNAME AS NICKNAME , U.GENDER_CODE AS GENDER_CODE , U.AGE AS AGE , U.IMAGE AS IMAGE , U.USE_YN AS USE_YN , U.SYS_MNGR_YN AS SYS_MNGR_YN , IFNULL(M.LVL_CODE, '') AS LVL_CODE FROM `USER` U LEFT JOIN MEMBERSHIP M ON M.MEMBERSHIP_KEY = U.MEMBERSHIP_KEY AND M.USE_YN <> 0 WHERE U.USER_ID = #{user.userId}
AND u.PASSWORD = #{user.password}
AND U.USE_YN = 1

/* 활동 정지 유저 체크 */
SELECT *
  FROM BLOCKED_USER
 WHERE EMAIL = #{email}
