mapper.select:
/* 브랜드 목록 조회 */ SELECT B.* , IFNULL(IFNULL(CM.MESSAGE, C.COUNTRY_NAME), '') AS COUNTRY_NAME , IFNULL(U.NICKNAME, '') AS UPDATER_NAME FROM BRAND B LEFT JOIN COUNTRY C ON C.COUNTRY_CODE = B.COUNTRY_CODE AND C.USE_YN = 1 LEFT JOIN COUNTRY_MESSAGE CM ON CM.MESSAGE_ID = C.COUNTRY_MESSAGE AND CM.COUNTRY_CODE = #{vo.locale} LEFT JOIN MESSAGE_GROUP MG ON MG.MESSAGE_ID = CM.MESSAGE_ID AND MG.USE_YN = 1 LEFT JOIN `USER` U ON U.USER_ID = B.UPDATER_ID
B.USE_YN = #{vo.useYn}
AND B.COUNTRY_CODE = #{vo.countryCode}
AND B.BRAND_KEY = #{vo.brandKey}
AND UPPER(B.LINK) LIKE CONCAT('%', UPPER(#{vo.link}), '%')

/* 브랜드 목록 전체 조회 */ SELECT B.BRAND_KEY AS BRAND_KEY , B.NAME AS NAME , IFNULL(B.LINK, '') AS LINK FROM BRAND B
UNION
SELECT B.BRAND_KEY AS BRAND_KEY , IFNULL(B.ENG_NAME, '') AS NAME , IFNULL(B.LINK, '') AS LINK FROM BRAND B

/* 브랜드 중복체크 */
SELECT COUNT(1)
  FROM BRAND
 WHERE UPPER(`NAME`)  = UPPER(#{vo.name})
   AND COUNTRY_CODE = #{vo.countryCode}

    AND BRAND_KEY <> #{vo.brandKey}

mapper.update:
/* 브랜드 수정 */
UPDATE BRAND
   SET COUNTRY_CODE = #{vo.countryCode}
     , `NAME`       = #{vo.name}
     , ENG_NAME     = #{vo.engName}
     , LINK         = #{vo.link}
     , `DESC`       = #{vo.desc}
     , USE_YN       = #{vo.useYn}
     , UPDATER_ID   = #{vo.updaterId}
     , UPDATE_DATE  = UTC_TIMESTAMP()
 WHERE BRAND_KEY = #{vo.brandKey}

/* 브랜드 삭제 */
UPDATE BRAND
   SET USE_YN      = 0
     , UPDATER_ID  = #{vo.updaterId}
     , UPDATE_DATE = UTC_TIMESTAMP()
 WHERE BRAND_KEY = #{vo.brandKey}

mapper.delete:
/* 매핑 삭제 */
DELETE
  FROM BRAND_CATEGORY_MAPPER
 WHERE BRAND_KEY = #{brandKey}
