mapper.select:
/* 카테고리전체 목록 조회 */
SELECT C.CATEGORY_ID                                        AS CATEGORY_ID
     , (
            SELECT CM.MESSAGE
              FROM COUNTRY_MESSAGE CM
             WHERE CM.MESSAGE_ID    = C.CATEGORY_MESSAGE
               AND CM.COUNTRY_CODE  = #{locale}
       )                                                    AS CATEGORY_NAME
  FROM CATEGORY C
 WHERE C.USE_YN         <> 0

/* 카테고리 목록 조회 */ SELECT C.* , ( SELECT CM.MESSAGE FROM COUNTRY_MESSAGE CM WHERE CM.MESSAGE_ID = C.CATEGORY_MESSAGE AND CM.COUNTRY_CODE = #{vo.locale} ) AS LOCALE_MESSAGE , ( SELECT U.NICKNAME FROM USER U WHERE U.USER_ID = C.UPDATER_ID AND U.SYS_MNGR_YN <> 0 ) AS UPDATER_NAME FROM CATEGORY C
AND C.CATEGORY_ID = UPPER(#{vo.categoryId})
AND ( C.CATEGORY_NAME LIKE CONCAT('%', #{vo.categoryName}, '%') OR CM.MESSAGE LIKE CONCAT('%', #{vo.categoryName}, '%') )
AND C.USE_YN = #{vo.useYn}

mapper.update:
/* 카테고리 수정 */
UPDATE CATEGORY
   SET CATEGORY_MESSAGE     = #{vo.categoryMessage}
     , CATEGORY_NAME        = #{vo.categoryName}
     , USE_YN               = #{vo.useYn}
     , UPDATER_ID           = #{vo.updaterId}
     , UPDATE_DATE          = UTC_TIMESTAMP()
 WHERE CATEGORY_ID = UPPER(#{vo.categoryId})

/* 카테고리 삭제 */
UPDATE CATEGORY
   SET USE_YN      = 0
     , UPDATER_ID  = #{vo.updaterId}
     , UPDATE_DATE = UTC_TIMESTAMP()
 WHERE CATEGORY_ID = UPPER(#{vo.categoryId})
