mapper.select:
/* 국가 목록 조회 */
SELECT C.* , IFNULL(CM.MESSAGE, C.COUNTRY_NAME) AS LOCALE_MESSAGE , IFNULL(U.NICKNAME, '') AS UPDATER_NAME FROM COUNTRY C LEFT JOIN COUNTRY_MESSAGE CM ON CM.MESSAGE_ID = C.COUNTRY_MESSAGE AND CM.COUNTRY_CODE = #{vo.locale} LEFT JOIN MESSAGE_GROUP MG ON MG.MESSAGE_ID = CM.MESSAGE_ID AND MG.USE_YN = 1 LEFT JOIN `USER` U ON U.USER_ID = C.UPDATER_ID
AND C.COUNTRY_CODE = UPPER(#{vo.countryCode})
AND ( C.COUNTRY_NAME LIKE CONCAT('%', #{vo.countryName}, '%') OR IFNULL(CM.MESSAGE, '') LIKE CONCAT('%', #{vo.countryName}, '%') )
AND C.USE_YN = #{vo.useYn}

mapper.update:
/* 국가 수정 */
UPDATE COUNTRY
   SET COUNTRY_MESSAGE      = #{vo.countryMessage}
     , COUNTRY_NAME         = #{vo.countryName}
     , USE_YN               = #{vo.useYn}
     , UPDATER_ID           = #{vo.updaterId}
     , UPDATE_DATE          = UTC_TIMESTAMP()
 WHERE COUNTRY_CODE = UPPER(#{vo.countryCode})

/* 국가 삭제 */
UPDATE COUNTRY
   SET USE_YN      = 0
     , UPDATER_ID  = #{vo.updaterId}
     , UPDATE_DATE = UTC_TIMESTAMP()
 WHERE COUNTRY_CODE = UPPER(#{vo.countryCode})
