mapper.select:
/* 국가전체 목록 조회 */
SELECT C.COUNTRY_CODE                                       AS COUNTRY_CODE
     , (
            SELECT CM.MESSAGE
              FROM COUNTRY_MESSAGE CM
             WHERE CM.MESSAGE_ID    = C.COUNTRY_MESSAGE
               AND CM.COUNTRY_CODE  = #{locale}
       )                                                    AS COUNTRY_NAME
  FROM COUNTRY C
 WHERE C.USE_YN         <> 0

/* 국가 목록 조회 */
SELECT C.*
     , (
           SELECT CM.MESSAGE
             FROM COUNTRY_MESSAGE CM
            WHERE CM.MESSAGE_ID    = C.COUNTRY_MESSAGE
              AND CM.COUNTRY_CODE  = #{vo.locale}
       )                                                AS LOCALE_MESSAGE
     , (
            SELECT U.NICKNAME
              FROM USER U
             WHERE U.USER_ID        = C.UPDATER_ID
               AND U.SYS_MNGR_YN    <> 0
       )                                                AS UPDATER_NAME
  FROM COUNTRY C


        AND C.COUNTRY_CODE = #{vo.countryCode}


        AND (
                   C.COUNTRY_NAME       LIKE CONCAT('%', #{vo.countryName}, '%')
                OR CM.MESSAGE           LIKE CONCAT('%', #{vo.countryName}, '%')
            )

    AND C.USE_YN = #{vo.useYn}

mapper.update:
/* 국가 수정 */
UPDATE COUNTRY
   SET COUNTRY_MESSAGE      = #{vo.countryMessage}
     , COUNTRY_NAME         = #{vo.countryName}
     , USE_YN               = #{vo.useYn}
     , UPDATER_ID           = #{vo.updaterId}
     , UPDATE_DATE          = UTC_TIMESTAMP()
 WHERE COUNTRY_CODE = UPPER(#{vo.countryCode})

/* 국가 삭제 */
UPDATE COUNTRY
   SET USE_YN      = 0
     , UPDATER_ID  = #{vo.updaterId}
     , UPDATE_DATE = UTC_TIMESTAMP()
 WHERE COUNTRY_CODE = UPPER(#{vo.countryCode})
