mapper.select:
/* 포럼 조회 */ SELECT F.* , IFNULL( ( SELECT CM.MESSAGE FROM COUNTRY_MESSAGE CM INNER JOIN MESSAGE_GROUP MG ON MG.MESSAGE_ID = CM.MESSAGE_ID AND MG.USE_YN <> 0 AND CM.COUNTRY_CODE = #{vo.locale} WHERE CM.MESSAGE_ID = CAT.CATEGORY_MESSAGE ), CAT.CATEGORY_NAME ) AS CATEGORY_NAME , IFNULL( ( SELECT CM.MESSAGE FROM COUNTRY_MESSAGE CM INNER JOIN MESSAGE_GROUP MG ON MG.MESSAGE_ID = CM.MESSAGE_ID AND MG.USE_YN <> 0 AND CM.COUNTRY_CODE = #{vo.locale} WHERE CM.MESSAGE_ID = C.CODE_MESSAGE ), C.CODE_MESSAGE ) AS FORUM_TYPE_NAME FROM FORUM F INNER JOIN CATEGORY CAT ON CAT.CATEGORY_ID = F.CATEGORY_ID
AND F.FORUM_KEY = #{vo.forumKey}
AND CAT.CATEGORY_ID = #{vo.categoryId}
AND F.FORUM_TYPE_CODE = #{vo.forumTypeCode}
AND F.USE_YN = #{vo.useYn} AND CAT.USE_YN <> 0 INNER JOIN CODE C ON C.CODE_ID = F.FORUM_TYPE_CODE AND C.CODE_GROUP_ID = 'FORUM_TYPE_CODE' AND C.USE_YN <> 0 INNER JOIN CODE_GROUP CG ON CG.CODE_GROUP_ID = C.CODE_GROUP_ID AND CG.USE_YN <> 0

/* 중복체크 */
SELECT COUNT(1)
  FROM FORUM
 WHERE CATEGORY_ID  = #{vo.categoryId}
   AND FORUM_NAME   = #{vo.forumName}

mapper.update:
/* 포럼 삭제 */
UPDATE FORUM
   SET USE_YN       = 0
     , UPDATE_DATE  = UTC_TIMESTAMP()
     , UPDATER_ID   = #{vo.updaterId}
 WHERE FORUM_KEY = #{vo.forumKey}

/* 포럼 수정 */
UPDATE FORUM
   SET USE_YN           = #{vo.useYn}
     , CATEGORY_ID      = #{vo.categoryId}
     , FORUM_NAME       = #{vo.forumName}
     , FORUM_TYPE_CODE  = #{vo.forumTypeCode}
     , REVIEW_YN        = #{vo.reviewYn}
     , UPDATE_DATE      = UTC_TIMESTAMP()
     , UPDATER_ID       = #{vo.updaterId}
 WHERE FORUM_KEY = #{vo.forumKey}
