mapper.select:
/* 포럼 목록 조회 */ SELECT F.* , IFNULL( ( SELECT CASE WHEN MIN(C.CODE_ID) <=
#{lvlCode}
'0'
THEN 0 ELSE 1 END FROM MEMBERSHIP_FORUM_MAPPER MFM INNER JOIN MEMBERSHIP M ON M.MEMBERSHIP_KEY = MFM.MEMBERSHIP_KEY AND M.USE_YN = 1 INNER JOIN `CODE` C ON C.CODE_ID = M.LVL_CODE AND C.CODE_GROUP_ID = 'MEMBERSHIP_LVL_CODE' AND C.USE_YN = 1 INNER JOIN CODE_GROUP CG ON CG.CODE_GROUP_ID = C.CODE_GROUP_ID AND CG.USE_YN = 1 WHERE MFM.FORUM_KEY = F.FORUM_KEY ), 1 ) AS DISABLED FROM FORUM F INNER JOIN CATEGORY C ON C.CATEGORY_ID = F.CATEGORY_ID AND F.USE_YN = 1 AND C.USE_YN = 1

/* 포럼 조회 */
SELECT F.* , IFNULL(CAT_CM.MESSAGE, CAT.CATEGORY_NAME) AS CATEGORY_NAME , IFNULL(C_CM.MESSAGE, C.CODE_MESSAGE) AS FORUM_TYPE_NAME , IFNULL(U.NICKNAME, '') AS UPDATER_NAME FROM FORUM F INNER JOIN CATEGORY CAT ON CAT.CATEGORY_ID = F.CATEGORY_ID
AND F.FORUM_KEY = #{vo.forumKey}
AND CAT.CATEGORY_ID = #{vo.categoryId}
AND F.FORUM_TYPE_CODE = #{vo.forumTypeCode}
AND F.USE_YN = #{vo.useYn} AND CAT.USE_YN = 1 INNER JOIN `CODE` C ON C.CODE_ID = F.FORUM_TYPE_CODE AND C.CODE_GROUP_ID = 'FORUM_TYPE_CODE' AND C.USE_YN = 1 INNER JOIN CODE_GROUP CG ON CG.CODE_GROUP_ID = C.CODE_GROUP_ID AND CG.USE_YN = 1 LEFT JOIN COUNTRY_MESSAGE CAT_CM ON CAT_CM.MESSAGE_ID = CAT.CATEGORY_MESSAGE AND CAT_CM.COUNTRY_CODE = #{vo.locale} LEFT JOIN MESSAGE_GROUP CAT_MG ON CAT_MG.MESSAGE_ID = CAT_CM.MESSAGE_ID AND CAT_MG.USE_YN = 1 LEFT JOIN COUNTRY_MESSAGE C_CM ON C_CM.MESSAGE_ID = C.CODE_MESSAGE AND C_CM.COUNTRY_CODE = #{vo.locale} LEFT JOIN MESSAGE_GROUP C_MG ON C_MG.MESSAGE_ID = C_CM.MESSAGE_ID AND C_MG.USE_YN = 1 LEFT JOIN `USER` U ON U.USER_ID = F.UPDATER_ID

/* 중복체크 */
SELECT COUNT(1)
  FROM FORUM
 WHERE CATEGORY_ID  = #{vo.categoryId}
   AND FORUM_NAME   = #{vo.forumName}

   AND FORUM_KEY    <> #{vo.forumKey}

mapper.update:
/* 포럼 삭제 */
UPDATE FORUM
   SET USE_YN       = 0
     , UPDATE_DATE  = UTC_TIMESTAMP()
     , UPDATER_ID   = #{vo.updaterId}
 WHERE FORUM_KEY = #{vo.forumKey}

/* 포럼 수정 */
UPDATE FORUM
   SET USE_YN           = #{vo.useYn}
     , CATEGORY_ID      = #{vo.categoryId}
     , FORUM_NAME       = #{vo.forumName}
     , FORUM_TYPE_CODE  = #{vo.forumTypeCode}
     , REVIEW_YN        = #{vo.reviewYn}
     , UPDATE_DATE      = UTC_TIMESTAMP()
     , UPDATER_ID       = #{vo.updaterId}
 WHERE FORUM_KEY = #{vo.forumKey}
