mapper.select:
/* 맴버쉽 목록 조회 */
SELECT M.* , IFNULL( ( SELECT CM.MESSAGE FROM COUNTRY_MESSAGE CM INNER JOIN MESSAGE_GROUP MG ON MG.MESSAGE_ID = CM.MESSAGE_ID AND MG.USE_YN <> 0 WHERE CM.MESSAGE_ID = M.MEMBERSHIP_MESSAGE AND CM.COUNTRY_CODE = #{vo.locale} ), M.MEMBERSHIP_NAME ) AS LOCALE_MESSAGE , IFNULL( ( SELECT IFNULL(CM.MESSAGE, C.CODE_NAME) FROM `CODE` C INNER JOIN CODE_GROUP CG ON CG.CODE_GROUP_ID = C.CODE_GROUP_ID AND CG.CODE_GROUP_ID = 'MEMBERSHIP_LVL_CODE' AND CG.USE_YN <> 0 AND C.USE_YN <> 0 LEFT JOIN COUNTRY_MESSAGE CM ON CM.MESSAGE_ID = C.CODE_MESSAGE AND CM.COUNTRY_CODE = #{vo.locale} AND CM.MESSAGE_ID IN ( SELECT MG.MESSAGE_ID FROM MESSAGE_GROUP MG WHERE MG.USE_YN <> 0 ) WHERE C.CODE_ID = M.LVL_CODE ), '' ) AS LVL_NAME , ( SELECT U.NICKNAME FROM `USER` U WHERE U.USER_ID = M.UPDATER_ID AND U.SYS_MNGR_YN <> 0 AND U.USE_YN <> 0 ) AS UPDATER_NAME FROM MEMBERSHIP M WHERE M.USE_YN = #{vo.useYn}
AND M.MEMBERSHIP_KEY = #{vo.membershipKey}
AND M.LVL_CODE = #{vo.lvlCode}

/* 중복체크 */
SELECT COUNT(1)
  FROM MEMBERSHIP
 WHERE LVL_CODE             = #{vo.lvlCode}
   AND MEMBERSHIP_MESSAGE   = #{vo.membershipMessage}

    AND MEMBERSHIP_KEY <> #{vo.membershipKey}

mapper.update:
/* 맴버쉽 삭제 */
UPDATE MEMBERSHIP
   SET USE_YN       = 0
     , UPDATE_DATE  = UTC_TIMESTAMP()
     , UPDATER_ID   = #{vo.updaterId}
 WHERE MEMBERSHIP_KEY = #{vo.membershipKey}

/* 맴버쉽 수정 */
UPDATE MEMBERSHIP
   SET USE_YN               = #{vo.useYn}
     , MEMBERSHIP_MESSAGE   = #{vo.membershipMessage}
     , MEMBERSHIP_NAME      = #{vo.membershipName}
     , LVL_CODE             = #{vo.lvlCode}
     , UPDATE_DATE          = UTC_TIMESTAMP()
     , UPDATER_ID           = #{vo.updaterId}
 WHERE MEMBERSHIP_KEY = #{vo.membershipKey}

mapper.delete:
/* 매핑 삭제 */
DELETE
  FROM MEMBERSHIP_FORUM_MAPPER
 WHERE MEMBERSHIP_KEY   = #{vo.membershipKey}
   AND CATEGORY_ID      = #{vo.categoryId}
