mapper.select:
/* 제품타입 조회 */ SELECT PT.* , IFNULL( ( SELECT CM.MESSAGE FROM COUNTRY_MESSAGE CM INNER JOIN MESSAGE_GROUP MG ON MG.MESSAGE_ID = CM.MESSAGE_ID AND MG.USE_YN <> 0 AND CM.COUNTRY_CODE = #{vo.locale} WHERE CM.MESSAGE_ID = PT.PRODUCT_TYPE_MESSAGE ), PT.PRODUCT_TYPE_NAME ) AS LOCALE_MESSAGE , IFNULL( ( SELECT CM.MESSAGE FROM COUNTRY_MESSAGE CM INNER JOIN MESSAGE_GROUP MG ON MG.MESSAGE_ID = CM.MESSAGE_ID AND MG.USE_YN <> 0 AND CM.COUNTRY_CODE = #{vo.locale} WHERE CM.MESSAGE_ID = CAT.CATEGORY_MESSAGE ), CAT.CATEGORY_NAME ) AS CATEGORY_NAME , ( SELECT U.NICKNAME FROM `USER` U WHERE U.USER_ID = PT.UPDATER_ID AND U.USE_YN <> 0 ) AS UPDATER_NAME FROM PRODUCT_TYPE PT INNER JOIN CATEGORY CAT ON CAT.CATEGORY_ID = PT.CATEGORY_ID
AND PT.PRODUCT_TYPE_KEY = #{vo.productTypeKey}
AND CAT.CATEGORY_ID = #{vo.categoryId}
AND PT.USE_YN = #{vo.useYn} AND CAT.USE_YN <> 0

/* 중복체크 */
SELECT COUNT(1)
  FROM PRODUCT_TYPE
 WHERE CATEGORY_ID          = #{vo.categoryId}
   AND PRODUCT_TYPE_NAME    = #{vo.productTypeName}

   AND PRODUCT_TYPE_KEY    <> #{vo.productTypeKey}

mapper.update:
/* 제품타입 삭제 */
UPDATE PRODUCT_TYPE
   SET USE_YN           = 0
     , UPDATE_DATE      = UTC_TIMESTAMP()
     , UPDATER_ID       = #{vo.updaterId}
 WHERE PRODUCT_TYPE_KEY = #{vo.productTypeKey}

/* 제품타입 수정 */
UPDATE PRODUCT_TYPE
   SET USE_YN               = #{vo.useYn}
     , CATEGORY_ID          = #{vo.categoryId}
     , PRODUCT_TYPE_NAME    = (
                                    SELECT CM.MESSAGE
                                      FROM COUNTRY_MESSAGE CM
                                     WHERE CM.COUNTRY_CODE = 'ENG'
                                       AND CM.MESSAGE_ID = #{vo.productTypeMessage}
                              )
     , PRODUCT_TYPE_MESSAGE = #{vo.productTypeMessage}
     , `DESC`               = #{vo.desc}
     , UPDATE_DATE          = UTC_TIMESTAMP()
     , UPDATER_ID           = #{vo.updaterId}
 WHERE PRODUCT_TYPE_KEY = #{vo.productTypeKey}
